package Lesson_0_2;

public class withChar {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char a = 'F';
		char b = 78;
		char c = '\u004b';
		char e = '\u0074';
		System.out.println("a = "+a);
		System.out.println("b = "+b);
		System.out.println("c = "+c);
		System.out.println("e = "+e+c);
		int f = 5;
		System.out.println(f);
		f = ++f+f++;
		System.out.println(f);
		
	}

}
